    private void btnregistrarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnregistrarActionPerformed
      DBServicio dbS = new DBServicio();
      probarConexionDB pcDB = new probarConexionDB();
      DBNivelParametros DBnp = new DBNivelParametros(); 
      String tipoServicio = txtTipoServicio.getText();
      String descripcionServicio = txtADescripcion.getText();
      int montoServicio = Integer.parseInt(txtMonto.getText());
      dbS.AgregarParametroServicio(pcDB.connection2(), tipoServicio);
      dbS.AgregarServicio(pcDB.connection2(), descripcionServicio, DBnp.Validar(pcDB.connection2(), tipoServicio), montoServicio);
      
    }//GEN-LAST:event_btnregistrarActionPerformed

